targets.expr:
sum(
  rate(container_cpu_usage_seconds_total{namespace="$namespace", container!="", image!=""}[$__rate_interval])
  * on(pod, namespace) group_left()
  kube_pod_info{namespace="$namespace", created_by_kind=~"$workload_kinds", created_by_name=~"$workload(-[a-z0-9]{8,10}|-[0-9]+)?"}
) * 1000

sum(
  kube_pod_container_resource_requests{namespace="$namespace", resource="cpu", container!=""}
  * on(pod, namespace) group_left()
  kube_pod_info{namespace="$namespace", created_by_kind=~"$workload_kinds", created_by_name=~"$workload(-[a-z0-9]{8,10}|-[0-9]+)?"}
